import ComposableArchitecture

@Reducer
struct CounterFeature {
  @ObservableState
  struct State {
  	@Presents var alert: AlertState<Action.Alert>?
    var count = 0
    var fact: String?
    var isLoading = false
    var isTimerRunning = false
  }

  enum Action {
    case decrementButtonTapped
    case factButtonTapped
    case factResponse(String)
    case incrementButtonTapped
    case timerTick
    case toggleTimerButtonTapped
  }

  enum CancelID { case timer }
  @Dependency(\.dismiss) var dismiss	// ì´ëŸ° ì˜ì¡´ì„±ì„ ê°€ì§€ëŠ” ê²ƒë„ ê°€ëŠ¥

  var body: some ReducerOf<Self> {
    Reduce { state, action in
      switch action {
      case .decrementButtonTapped:
        state.count -= 1
        state.fact = nil
        return .none

      case .factButtonTapped:
        state.fact = nil
        state.isLoading = true
        return .run { [count = state.count] send in	// ë¹„ë™ê¸° ì‘ì—…ì¼ ê²½ìš° .run ì‚¬ìš©
          let (data, _) = try await URLSession.shared
            .data(from: URL(string: "http://numbersapi.com/\(count)")!)
          let fact = String(decoding: data, as: UTF8.self)
          await send(.factResponse(fact))	// ì—¬ê¸°ì„œ ë°”ë¡œ stateë¥¼ ìˆ˜ì •í•˜ë©´ ì•ˆë¨. Actionì„ ì´ìš©í•˜ì—¬ ì²˜ë¦¬
        }

      case let .factResponse(fact):
        state.fact = fact
        state.isLoading = false
        return .none

      case .incrementButtonTapped:
        state.count += 1
        state.fact = nil
        return .none

      case .timerTick:
        state.count += 1
        state.fact = nil
        return .none

      case .toggleTimerButtonTapped:
        state.isTimerRunning.toggle()
        if state.isTimerRunning {
          return .run { send in
            while true {
              try await Task.sleep(for: .seconds(1))
              await send(.timerTick)
            }
          }
          .cancellable(id: CancelID.timer)
        } else {
          return .cancel(id: CancelID.timer)
        }
      }
    }
    .ifLet(\.$alert, action: \.alert) // í•´ë‹¹ ê°’ì´ ì—†ë‹¤ë©´ ë¦¬ë“€ì„œ ì‘ë™í•¨
  }
}

